import React, {Fragment,useState} from "react";

const EditEvent = ({event}) => {

    const [name,setName] = useState(event.name);
    const [description,setDescription] = useState(event.description);
    const [location, setLocation] = useState(event.location);
    const [date, setDate] = useState(event.date);

    //edit description function

    const updateName = async (e) => {
        e.preventDefault();
            try {
                const body = {name};
                const response = await fetch(`http://localhost:4000/events/${event.id}`,{
                    method: "PUT",
                    headers: {"Content-Type": "application/json"},
                    body: JSON.stringify(body)
                });
                window.location
            } catch (err) {
                console.error(err.message);
            }
    }

    return (
      <Fragment>
        <button
          type="button"
          class="btn btn-warning"
          data-toggle="modal"
          data-target={`#id${event.id}`}
        >
          Edit
        </button>

        {
            /*
                id=id10
            */
        }

        <div class="modal" id={`id${event.id}`}>
          <div class="modal-dialog">
            <div class="modal-content">
              <div class="modal-header">
                <h4 class="modal-title">Edit Event</h4>
                <button type="button" class="close" data-dismiss="modal">
                  &times;
                </button>
              </div>

              <div class="modal-body">
                <input type="text" className="form-control" value={name} onChange={e => setName(e.target.value)}/>
                <input type="text" className="form-control" value={description}onChange={f => setName(f.target.value)} />
                <input type="text" className="form-control" value={date} onChange={g => setName(g.target.value)} />
                <input type="text" className="form-control" value={location} onChange={h => setName(h.target.value)}/>
              </div>

              <div class="modal-footer">
                <button
                  type="button"
                  class="btn btn-warning"
                  data-dismiss="modal"
                  onClick={e => updateName(e) }
                >
                  Edit
                </button>
              </div>
              <div class="modal-footer">
                <button
                  type="button"
                  class="btn btn-danger"
                  data-dismiss="modal"
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
      </Fragment>
    );
};

export default EditEvent;
