import React, { Fragment, useEffect, useState } from "react";

const ListTasks = () => {
  const [tasks, setTasks] = useState([]);

  // Mark task as complete
  const markTaskComplete = async (id) => {
    try {
      const response = await fetch(`http://localhost:4000/tasks/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ status: "Completed" }), // Sending the status to update
      });

      if (response.ok) {
        const updatedTask = await response.json(); // Get the updated task data
        // Update the task status in the state
        setTasks(
          tasks.map((task) =>
            task.id === id ? { ...task, status: updatedTask.task.status } : task
          )
        );
      } else {
        console.error("Failed to update task status.");
      }
    } catch (err) {
      console.error(err.message);
    }
  };

  // Fetch all tasks
  const getTasks = async () => {
    try {
      const response = await fetch("http://localhost:4000/tasks");
      const jsonData = await response.json();
      setTasks(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    getTasks();
  }, []);

  return (
    <Fragment>
      <div className="container">
        <h2 className="text-center mt-5">List of Tasks</h2>

        <table className="table">
          <thead>
            <tr>
              <th>Task Name</th>
              <th>Description</th>
              <th>Deadline</th>
              <th>Status</th>
              <th>Event</th>
              <th>Complete</th>
            </tr>
          </thead>
          <tbody>
            {tasks.length > 0 ? (
              tasks.map((task) => (
                <tr key={task.id}>
                  <td>{task.name}</td>
                  <td>{task.description}</td>
                  <td>{task.deadline}</td>
                  <td>{task.status}</td>
                  <td>{task.event_name || "No Event"}</td>
                  <td>
                    {task.status !== "Completed" ? (
                      <button
                        className="btn btn-success"
                        onClick={() => markTaskComplete(task.id)}
                      >
                        Complete
                      </button>
                    ) : (
                      "Completed"
                    )}
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="6" className="text-center">
                  No tasks available
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </Fragment>
  );
};

export default ListTasks;
