import React, { Fragment, useEffect, useState } from "react";

const deleteEvent = async (id) => {
  try {
    // Sending a DELETE request to the backend
    const response = await fetch(`http://localhost:4000/events/${id}`, {
      method: "DELETE",
    });

    if (response.ok) {
      // Update the local state by filtering out the deleted event
      setEvent(events.filter((event) => event.id !== id));
    } else {
      console.error("Failed to delete the event");
    }
  } catch (err) {
    console.error(err.message);
  }
};


const ListEvent = () => {
  // Change the function name to ListEvent

  const [events, setEvent] = useState([]);

  const getEvent = async () => {
    try {
      const response = await fetch("http://localhost:4000/events");
      const jsonData = await response.json();

      setEvent(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    getEvent();
  }, []);

  return (
    <Fragment>
      <div className="container">
        <h2 className="text-center mt-5">List of upcoming Events</h2>

        <table className="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Description</th>
              <th>Location</th>
              <th>Date</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {events.map((event) => (
              <tr key={event.id}>
                {" "}
                {/* Assuming each event has a unique 'id' */}
                <td>{event.name}</td> {/* Display event name */}
                <td>{event.description}</td> {/* Display event description */}
                <td>{event.location}</td> {/* Display event location */}
                <td>{event.date}</td> {/* Display event date */}
                <td>
                  <EditEvent event={event} /> {/* Edit button or component */}
                </td>
                <td>
                  <button
                    className="btn btn-danger"
                    onClick={() => deleteEvent(event.id)} // Replace with your delete function
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </Fragment>
  );
};

export default ListEvent;
