import React, { Fragment, useState, useEffect } from "react";

const InputAttendee = () => {
  const [name, setAttendeeName] = useState("");
  const [task, setAttendeeTask] = useState("");
  const [id, setEventId] = useState(""); // Store event ID (renamed from eventId)
  const [events, setEvents] = useState([]); // Store available events

  // Fetch events from the backend to display as options
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await fetch("http://localhost:4000/events"); // Adjust endpoint if needed
        const data = await response.json();
        setEvents(data);
      } catch (err) {
        console.error("Error fetching events:", err);
      }
    };
    fetchEvents();
  }, []);

  const onSubmitForm = async (e) => {
    e.preventDefault();
    try {
      const body = { name, task, id }; // Include id as the event ID
      const response = await fetch("http://localhost:4000/attendees/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });

      console.log(response);

      // Refresh page after adding attendee
      window.location = "/";
    } catch (err) {
      console.error("Error adding attendee:", err);
    }
  };

  return (
    <Fragment>
      <h1 className="text-center mt-5">Attendee Management</h1>
      <form className="d-flex flex-column mt-5" onSubmit={onSubmitForm}>
        <div className="mb-3">
          <label htmlFor="name" className="form-label">
            Attendee Name
          </label>
          <input
            type="text"
            id="name"
            className="form-control"
            value={name}
            onChange={(e) => setAttendeeName(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="task" className="form-label">
            Assigned Task
          </label>
          <input
            type="text"
            id="task"
            className="form-control"
            value={task}
            onChange={(e) => setAttendeeTask(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="id" className="form-label">
            Select Event
          </label>
          <select
            id="id"
            className="form-control"
            value={id}
            onChange={(e) => setEventId(e.target.value)}
            required
          >
            <option value="">Select Event</option>
            {events.map((event) => (
              <option key={event.id} value={event.id}>
                {event.name}
              </option>
            ))}
          </select>
        </div>
        <button className="btn btn-success">Add Attendee</button>
      </form>
    </Fragment>
  );
};

export default InputAttendee;
