import React, { useState, useEffect } from "react";

const TaskCreation = ({ events, attendees }) => {
  const [newTask, setNewTask] = useState({
    name: "",
    description: "",
    deadline: "",
    attendee_id: "",
  });
  const [eventId, setEventId] = useState(""); // Selected event ID
  const [status, setStatus] = useState("Pending"); // Default status
  const [message, setMessage] = useState("");

  const handleInputChange = (e) => {
    setNewTask({ ...newTask, [e.target.name]: e.target.value });
  };

  const handleCreateTask = async (e) => {
    e.preventDefault();
    try {
      const body = { ...newTask, event_id: eventId, status }; // Combine task details
      const response = await fetch("http://localhost:4000/tasks", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });

      if (!response.ok) {
        const errorData = await response.json();
        console.error("Error adding task:", errorData);
        throw new Error(errorData.message || "Failed to add task");
      }

      const result = await response.json();
      setMessage(`Task '${result.name}' added successfully!`);

      // Reset form fields
      setNewTask({
        name: "",
        description: "",
        deadline: "",
        attendee_id: "",
      });
      setEventId("");
      setStatus("Pending");
    } catch (err) {
      console.error("Error adding task:", err);
      setMessage("Error adding task. Please try again.");
    }
  };

  return (
    <div>
      <h1>Create a New Task</h1>
      {message && <p>{message}</p>}
      <form onSubmit={handleCreateTask}>
        <input
          type="text"
          name="name"
          value={newTask.name}
          onChange={handleInputChange}
          placeholder="Task Name"
          required
        />
        <textarea
          name="description"
          value={newTask.description}
          onChange={handleInputChange}
          placeholder="Task Description"
        ></textarea>
        <input
          type="date"
          name="deadline"
          value={newTask.deadline}
          onChange={handleInputChange}
        />
        <select
          name="eventId"
          value={eventId}
          onChange={(e) => setEventId(e.target.value)}
          required
        >
          <option value="">Select an Event</option>
          {events.map((event) => (
            <option key={event.id} value={event.id}>
              {event.name}
            </option>
          ))}
        </select>
        <select
          name="attendee_id"
          value={newTask.attendee_id}
          onChange={handleInputChange}
        >
          <option value="">Assign an Attendee</option>
          {attendees.map((attendee) => (
            <option key={attendee.id} value={attendee.id}>
              {attendee.name}
            </option>
          ))}
        </select>
        <button type="submit">Create Task</button>
      </form>
    </div>
  );
};

export default TaskCreation;
